const PokemonCard = ({ pokemon, langage }) => {
    return (
        <div class="flex bg-white justify-center rounded-3xl p-[2rem] m-8 w-[30rem] h-[30rem] shadow-navbar">
            <div class="w-full">
                <div class="flex rounded-3xl justify-center items-center bg-slate-200 h-1/2 mb-3">
                    <img src={pokemon.image} alt={pokemon.names[langage]} class="h-[100%]"/>
                </div>
                <h6 class="font-titre text-3xl text-gray-700 text-center underline decoration-red-400 decoration-2">{pokemon.names[langage]}</h6>
                <div sx={{ marginTop: 1 }}>
                    <li sx={{ margin: '2px' }}>
                        {pokemon.types && pokemon.types.map((type) => (
                            <li>{type}</li>
                        ))}
                    </li>
                </div>
            </div>
        </div>
    );
};



export default PokemonCard;
