import React from "react";
import Slider from "react-slick";
import { useNavigate } from "react-router-dom";

function PokemonSlider({ pokemonData, pokemonID, language }) {
    const navigate = useNavigate();
    const initialIndex = pokemonData.findIndex(poke => poke.id === pokemonID);

    const settings = {
        infinite: true,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 1,
        swipe: true,
        swipeToSlide: true,
        centerMode: true,
        touchThreshold: 10,
        initialSlide: initialIndex,
        afterChange: (current) => {
            const newPokemonID = pokemonData[current].id;
            navigate(`/pokemon/${newPokemonID}`);
        },
    };

    return (
        <div className="relative">
            <Slider {...settings}>
                {pokemonData.map((poke) => (
                    <div key={poke.id} className="p-2">
                        <img
                            src={poke.image}
                            alt={poke.names[language]}
                            className="h-26 w-26 object-cover rounded-full mx-auto"
                        />
                    </div>
                ))}
            </Slider>
            <div className="absolute top-0 left-1/2 -translate-x-1/2 -translate-y-1/2 w-1 h-10 bg-black z-10 pointer-events-none"></div>
        </div>
    );
}

export default PokemonSlider;
