import Slider from "react-slick";

function PokemonSlider({ pokemonData, language }) {
    const settings = {
        dots: true,
        infinite: true,
        speed: 700,
        slidesToShow: 3,
        slidesToScroll: 1,
        responsive: [
            { breakpoint: 768, settings: { slidesToShow: 2 } },
            { breakpoint: 480, settings: { slidesToShow: 1 } },
        ],
    };

    return (
        <Slider {...settings}>
            {pokemonData.map((poke) => (
                <div key={poke.id} className="p-2">
                    <img
                        src={poke.image}
                        alt={poke.names[language]}
                        className="h-28 w-28 object-cover rounded-full mx-auto"
                    />
                </div>
            ))}
        </Slider>
    );
}

export default PokemonSlider;
