import React, { useState } from "react";
import Slider from "react-slick";
import { useNavigate } from "react-router-dom";


function PokemonSlider({ pokemonData, pokemonID, language }) {

    const navigate = useNavigate();
    const [currentSlide, setCurrentSlide] = useState(pokemonID-1);
    const initialIndex = pokemonData.findIndex(poke => poke.id === pokemonID);

    const settings = {
        infinite: true,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 1,
        swipe: true,
        swipeToSlide: true,
        centerMode: true,
        touchThreshold: 10, 
        initialSlide: initialIndex,
        beforeChange: (current, next) => {
            setCurrentSlide(next);
        },
    };

    React.useEffect(() => {
        if (currentSlide >= 0 && currentSlide < pokemonData.length) {
            const newPokemonID = pokemonData[currentSlide].id;
            navigate(`/pokemon/${newPokemonID}`);
        }
    }, [currentSlide, pokemonData, navigate]);

    return (
        <Slider {...settings}>
            {pokemonData.map((poke) => (
                <div key={poke.id} className="p-2">
                    <img
                        src={poke.image}
                        alt={poke.names[language]}
                        className="h-26 w-26 object-cover rounded-full mx-auto"
                    />
                </div>
            ))}
        </Slider>
    );
}

export default PokemonSlider;
