import Slider from "react-slick";


function PokemonSlider({ pokemonData, pokemonID, language }) {

    const selectedPokemon = pokemonData.findIndex(poke => poke.id === pokemonID);

    const settings = {
        infinite: true,
        speed: 500,
        slidesToShow: 9,
        slidesToScroll: 1,
        swipe: true,
        swipeToSlide: true,
        touchThreshold: 10, 
        initialSlide: selectedPokemon
    };

    return (
        <Slider {...settings}>
            {pokemonData.map((poke) => (
                <div key={poke.id} className="p-2">
                    <img
                        src={poke.image}
                        alt={poke.names[language]}
                        className="h-26 w-26 object-cover rounded-full mx-auto"
                    />
                </div>
            ))}
        </Slider>
    );
}

export default PokemonSlider;
