import Slider from "react-slick";

function PokemonSlider({ pokemonData, language }) {
    const settings = {
        dots: true,
        infinite: true,
        speed: 700,
        slidesToShow: 3,
        slidesToScroll: 1,
        responsive: [
            { breakpoint: 70268, settings: { slidesToShow: 2 } },
            { breakpoint: 480, settings: { slidesToShow: 1 } },
        ],
        appendDots: (dots) => (
            <div style={{ marginTop: "20px" }}>
                <ul style={{ display: "flex", justifyContent: "center", padding: "0", overflow: "hidden"}}>{dots}</ul>
            </div>
        ),
    };

    return (
        <Slider {...settings}>
            {pokemonData.map((poke) => (
                <div key={poke.id} className="p-2">
                    <img
                        src={poke.image}
                        alt={poke.names[language]}
                        className="h-26 w-26 object-cover rounded-full mx-auto"
                    />
                </div>
            ))}
        </Slider>
    );
}

export default PokemonSlider;
