import React from 'react';
import { motion, useAnimation } from 'framer-motion';
import { useEffect } from 'react';

function BouncingPokeball() {
    const controls = useAnimation();

    useEffect(() => {
        const bounceAnimation = async () => {
            while (true) {
                await controls.start({
                    y: [0, 300],  // Tombe à 300px en bas
                    transition: {
                        duration: 1,
                        ease: "easeIn",  // Effet de gravité
                    },
                });
                await controls.start({
                    y: [300, 0],  // Remonte à la position d'origine
                    transition: {
                        duration: 0.6,
                        ease: "easeOut",  // Effet de rebond plus rapide
                    },
                });
            }
        };
        bounceAnimation();
    }, [controls]);

    return (
        <motion.img
            src="https://img.icons8.com/emoji/48/pokeball.png"
            alt="Pokeball"
            animate={controls}
            style={{
                position: "fixed",
                bottom: "20px",
                left: "20px",
                width: "48px",
                height: "48px",
            }}
        />
    );
}

export default BouncingPokeball;
