import Slider from "react-slick";

function CustomArrow({ className, style, onClick, direction }) {
    return (
        <div
            className={`${className} custom-arrow`}
            style={{
                ...style,
                display: "block",
                position: "absolute",
                top: "",
                transform: "translateY(-50%)",
                zIndex: 2,
                [direction]: "5px",
                cursor: "pointer",
            }}
            onClick={onClick}
        >
            <div
                style={{
                    width: "0",
                    height: "0",
                    borderStyle: "solid",
                    borderWidth: direction === "left" ? "15px 20px 15px 0" : "15px 0 15px 20px",
                    borderColor:
                        direction === "left"
                            ? "transparent #000 transparent transparent"
                            : "transparent transparent transparent #000",
                }}
            ></div>
        </div>
    );
}

function PokemonSlider({ pokemonData, language }) {
    const settings = {
        dots: true,
        infinite: true,
        speed: 700,
        slidesToShow: 9,
        slidesToScroll: 1,
        appendDots: (dots) => (
            <div style={{ }}>
                <ul style={{ display: "flex", justifyContent: "center", paddingTop: "", overflow: "hidden", flexWrap: "wrap"}}>{dots}</ul>
            </div>
        ),
    };

    return (
        <Slider {...settings}>
            {pokemonData.map((poke) => (
                <div key={poke.id} className="p-2">
                    <img
                        src={poke.image}
                        alt={poke.names[language]}
                        className="h-26 w-26 object-cover rounded-full mx-auto"
                    />
                </div>
            ))}
        </Slider>
    );
}

export default PokemonSlider;
