import { useLanguage } from './hooks/useLanguage';

function LanguageSelection({ onLanguageChange }) {

    const { language, changeLanguage } = useLanguage();

    const handleLanguageChange = (e) => {
        const selectedLanguage = e.target.value;
        changeLanguage(selectedLanguage);
    };

    return (
        <li class="m-0 flex align-center">
            <select onChange={handleLanguageChange} className="fp-[1rem] px-[2rem] inline-block text-[1.6rem] uppercase font-bold text-white rounded-xl bg-[#001340] cursor-pointer">
                <option value="fr">FR</option>
                <option value="en">EN</option>
                <option value="de">DE</option>
                <option value="es">ES</option>
                <option value="it">IT</option>
                <option value="ko">KO</option>
                <option class="rounded-br-lg rounded-bl-lg" value="ja">JP</option>
            </select>
        </li>
    );
}

export default LanguageSelection;
