const PokemonCard = ({ pokemon, langage }) => {
    return (
        <div class="flex bg-white justify-center rounded-3xl p-[2rem] m-8 w-[30rem] h-[30rem] shadow-navbar">
            <div sx={{ textAlign: 'center' }}>
                <div class="flex rounded-3xl justify-center items-center bg-slate-200 w-full h-1/2">
                    <img src={pokemon.image} alt={pokemon.names[langage]}/>
                </div>
                <h6>{pokemon.names[langage]}</h6>
                <div sx={{ marginTop: 1 }}>
                    <li sx={{ margin: '2px' }}>{pokemon.types}</li>
                </div>
            </div>
        </div>
    );
};



export default PokemonCard;
