import { useState } from 'react';

export const useLanguage = () => {
    const [language, setLanguage] = useState('fr'); // Langue par défaut

    const changeLanguage = (newLanguage) => {
        setLanguage(newLanguage); // Mise à jour de la langue
    };

    return {
        language,
        changeLanguage,
    };
};
