import { useState } from 'react';

export const useLanguage = () => {
    const [language, setLanguage] = useState('fr');

    const changeLanguage = (newLanguage) => {
        setLanguage(newLanguage);
    };

    return {
        language,
        changeLanguage,
    };
};
